import './mapa.css';
import React from 'react';
import { Icon } from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import data from './huertos.json';
import {Spinner} from 'spin.js';


const icon = new Icon({
  iconUrl: 'https://image.flaticon.com/icons/png/512/2227/2227504.png',
  iconSize: [50, 50],
  iconAnchor: [20, 40],
  shadowUrl: "https://unpkg.com/leaflet@1.6/dist/images/marker-shadow.png",
  shadowSize: [50, 50],
  shadowAnchor: [32, 25],
  popupAnchor:  [-5, -29]
});

const opts = {
  lines: 14, // The number of lines to draw
  length: 43, // The length of each line
  width: 35, // The line thickness
  radius: 27, // The radius of the inner circle
  scale: 1.05, // Scales overall size of the spinner
  corners: 1, // Corner roundness (0..1)
  speed: 0.8, // Rounds per second
  rotate: 0, // The rotation offset
  animation: 'spinner-line-fade-quick', // The CSS animation name for the lines
  direction: 1, // 1: clockwise, -1: counterclockwise
  color: '#00ffc2', // CSS color or array of colors
  fadeColor: 'transparent', // CSS color or array of colors
  top: '50%', // Top position relative to parent
  left: '50%', // Left position relative to parent
  shadow: '0 0 1px transparent', // Box-shadow for the lines
  zIndex: 2000000000, // The z-index (defaults to 2e9)
  className: 'spinner', // The CSS class to assign to the spinner
  position: 'absolute', // Element positioning
};

const target = document.getElementById('foo');
const spinner = new Spinner(opts).spin(target);

export default function Mapa(){


  return (
    <MapContainer center={[19.427074398015293, -99.12289930564387]} zoom={6}>

      <TileLayer
        url="https://stamen-tiles-{s}.a.ssl.fastly.net/watercolor/{z}/{x}/{y}.png"
        attribution='Map tiles by <a href="http://stamen.com">Stamen Design</a>, <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a> &mdash; Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <TileLayer
        url="https://stamen-tiles-{s}.a.ssl.fastly.net/toner-labels/{z}/{x}/{y}{r}.png"
      />

      {data &&
        data.map(({nombre, descripcion, tipo, latitud, longitud, redes_sociales, id}, i) => (
          <Marker
            key={id}
            position={[latitud, longitud]}
            icon={icon}>

          <Popup className="popup-text">
            <h5><b>{nombre}</b></h5>
            <hr></hr>
            <b>Tipo de huerto:</b> {tipo}
            <br></br>
            <br></br>
            <b>Descripci√≥n: </b>{descripcion}
            <br></br>
            <br></br>
            <a href={redes_sociales} target="_blank" rel='noreferrer noopener' >Redes Sociales</a>
          </Popup>
          </Marker>
        ))}


      </MapContainer>
  );
}
