const icon = new Icon({
  iconUrl: 'https://image.flaticon.com/icons/png/512/2227/2227504.png',
  iconSize: [40, 40],
  iconAnchor: [20, 40],
  shadowUrl: "https://image.flaticon.com/icons/png/512/459/459666.png",
  shadowSize: [40, 40],
  shadowAnchor: [26, 22],
  popupAnchor:  [-10, -35]
});

//d3.csv(data, function(data) { console.log(data); });


export default function App(){

  const getData = () =>
    Papa.parse(csv, {
      header: true,
      skipEmptyLines: true,
      complete: results => {
        return results.data;
      }
  });

  const data = getData().data;
  console.log(data);

  return (
    <MapContainer center={[19.427074398015293, -99.12289930564387]} zoom={12}>

      <TileLayer
        url="https://stamen-tiles-{s}.a.ssl.fastly.net/watercolor/{z}/{x}/{y}.png"
        attribution='Map tiles by <a href="http://stamen.com">Stamen Design</a>, <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a> &mdash; Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <TileLayer
        url="https://stamen-tiles-{s}.a.ssl.fastly.net/toner-labels/{z}/{x}/{y}{r}.png"
      />

      {data &&
        data.map(({nombre, descripcion, tipo, latitud, longitud, redes_sociales}, i) => (
          <Marker
            key={'markers-${i}'}
            position={[latitud, longitud]}
            icon={icon}>

          <Popup>
            <p><b>{nombre}</b></p>
            <p><b>{nombre}</b></p>
            <p><b>{nombre}</b></p>
          </Popup>
          </Marker>
        ))}


